
public class Pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i,j;
		for(i=1;i<=5;i++)
		{
			
			
			for(j=1;j<=i;j++)
			{
				if((i+j)%2==0)
				{
					System.out.println(" 1 " );
				}
				
				else
				
					System.out.println(" 0 ");
				
						
			}
			System.out.println("\n");
	}
		
}
}
