
public class Pattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i,j;
		for(i=1;i<=4;i++)
		{
			
			
			for(j=1;j<=4;j++)
			{
				if(i==1||j==1||i==4||j==4)
				{
					System.out.println(" * " );
				}
				
				
			System.out.println("");
	}
		
			
		}	
	}
}
